import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [teams, setTeams] = useState([]);
  const [selectedTeams, setSelectedTeams] = useState([]);
  const [results, setResults] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/teams')
      .then(res => res.json())
      .then(data => {
        console.log('Teams loaded:', data);
        setTeams(data);
      })
      .catch(err => console.error('Error fetching teams:', err));
  }, []);

  const handleTeamSelect = (event) => {
    const selected = Array.from(event.target.selectedOptions, option => option.value);
    setSelectedTeams(selected);

    if (selected.length > 0) {
      console.log('Analyzing teams:', selected);
      fetch('http://localhost:5000/api/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ teams: selected }),
      })
        .then(res => res.json())
        .then(data => {
          console.log('Analysis results:', data);
          setResults(data);
        })
        .catch(err => console.error('Error analyzing packages:', err));
    } else {
      setResults([]);
    }
  };

  // Calculate yearly vs monthly costs
  const calculateYearlyVsMonthly = (result) => {
    const yearlyTotal = result.yearly_cost || result.package.yearly_price;
    const monthlyTotal = result.monthly_cost || result.package.monthly_price;
    const yearlyViaMonthly = monthlyTotal * 12;
    const savings = yearlyViaMonthly - yearlyTotal;
    return { yearlyTotal, monthlyTotal, yearlyViaMonthly, savings };
  };

  return (
    <div className="App">
      <h1>Streaming Package Comparison</h1>

      <div className="team-selector">
        <h2>Select Teams</h2>
        <select 
          multiple 
          onChange={handleTeamSelect}
          size={10}
          className="team-select"
        >
          {teams.map(team => (
            <option key={team} value={team}>
              {team}
            </option>
          ))}
        </select>
      </div>

      {selectedTeams.length > 0 && (
        <div className="results">
          <h2>Package Rankings</h2>
          <div className="package-rankings">
            {results
              .sort((a, b) => b.coverage_percentage - a.coverage_percentage)
              .map((result, index) => (
                <div key={index} className="package-card">
                  <div className="rank-badge">{index + 1}</div>
                  <h3>{result.package.name}</h3>
                  <div className="coverage-bar-container">
                    <div 
                      className="coverage-bar" 
                      style={{width: `${result.coverage_percentage}%`}}
                    >
                      {result.coverage_percentage.toFixed(1)}%
                    </div>
                  </div>
                  <div className="package-details">
                    <div className="games-coverage">
                      <p>Games covered: {result.covered_games} / {result.total_games}</p>
                      <p className="coverage-note">
                        {result.coverage_percentage < 100 
                          ? "⚠️ Doesn't cover all games" 
                          : "✅ Full coverage"}
                      </p>
                    </div>
                    <div className="pricing-comparison">
                      <div className="monthly-price">
                        <h4>Monthly</h4>
                        <p>€{result.package.monthly_price}/month</p>
                        <p className="yearly-equivalent">
                          (€{(result.package.monthly_price * 12).toFixed(2)}/year)
                        </p>
                      </div>
                      <div className="yearly-price">
                        <h4>Yearly</h4>
                        <p>€{result.package.yearly_price}/year</p>
                        <p className="monthly-equivalent">
                          (€{(result.package.yearly_price / 12).toFixed(2)}/month)
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
          </div>

          {results.some(r => r.is_combination) && (
            <>
              <h2>Best Package Combinations</h2>
              <div className="package-combinations">
                {results
                  .filter(r => r.is_combination)
                  .map((result, index) => {
                    const costs = calculateYearlyVsMonthly(result);
                    return (
                      <div key={index} className="package-card combination">
                        <h3>Combination {index + 1}</h3>
                        <div className="coverage-bar-container">
                          <div 
                            className="coverage-bar" 
                            style={{width: `${result.coverage_percentage}%`}}
                          >
                            {result.coverage_percentage.toFixed(1)}%
                          </div>
                        </div>
                        <div className="package-details">
                          <div className="packages-list">
                            <h4>Required Packages:</h4>
                            <ul>
                              <li className="main-package">
                                <strong>{result.package.name}</strong>
                                <span>€{result.package.yearly_price}/year</span>
                              </li>
                              {result.additional_packages?.map((pkg, i) => (
                                <li key={i}>
                                  <strong>{pkg.name}</strong>
                                  <span>€{pkg.yearly_price}/year</span>
                                </li>
                              ))}
                            </ul>
                          </div>
                          <div className="cost-comparison">
                            <div className="yearly-subscription">
                              <h4>Yearly Subscription</h4>
                              <p className="total-cost">€{costs.yearlyTotal}</p>
                            </div>
                            <div className="monthly-subscription">
                              <h4>Monthly Subscription</h4>
                              <p>€{costs.monthlyTotal}/month</p>
                              <p className="yearly-equivalent">
                                (€{costs.yearlyViaMonthly}/year)
                              </p>
                              {costs.savings > 0 && (
                                <p className="savings">
                                  Save €{costs.savings.toFixed(2)} with yearly subscription
                                </p>
                              )}
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
