import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import DatePicker from 'react-datepicker';
import { MagnifyingGlassIcon, CalendarIcon } from '@heroicons/react/24/outline';
import "react-datepicker/dist/react-datepicker.css";
import './App.css';

function GamesList({ games }) {
  return (
    <div className="space-y-4">
      <h3 className="text-xl font-semibold text-gray-800">Matched Games</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {games.map(game => (
          <div key={game.id} className="game-card">
            <div className="text-blue-600 font-medium">
              {new Date(game.date).toLocaleString()}
            </div>
            <div className="flex items-center justify-between mt-2">
              <span className="font-semibold text-gray-800">{game.home_team}</span>
              <span className="text-gray-500 px-2">vs</span>
              <span className="font-semibold text-gray-800">{game.away_team}</span>
            </div>
            <div className="mt-2 text-sm text-gray-600 italic">
              {game.tournament}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function App() {
  const [teams, setTeams] = useState([]);
  const [leagues, setLeagues] = useState([]);
  const [selectedTeams, setSelectedTeams] = useState([]);
  const [selectedLeagues, setSelectedLeagues] = useState([]);
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const [results, setResults] = useState({ single_packages: [], minimum_combination: null });
  const [matchedGames, setMatchedGames] = useState([]);

  const fetchFilteredLeagues = (selectedTeams) => {
    const teamValues = selectedTeams.map(team => team.value);

    fetch('http://localhost:5000/api/leagues/filtered', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ teams: teamValues }),
    })
      .then(res => res.json())
      .then(data => {
        const leagueOptions = data.map(league => ({
          value: league,
          label: league
        }));
        setLeagues(leagueOptions);
        setSelectedLeagues([]);
      })
      .catch(err => console.error('Error fetching filtered leagues:', err));
  };

  useEffect(() => {
    fetch('http://localhost:5000/api/teams')
      .then(res => res.json())
      .then(data => {
        const teamOptions = data.map(team => ({
          value: team,
          label: team
        }));
        setTeams(teamOptions);
      })
      .catch(err => console.error('Error fetching teams:', err));
  }, []);

  const handleTeamChange = (newSelectedTeams) => {
    setSelectedTeams(newSelectedTeams);
    fetchFilteredLeagues(newSelectedTeams);
  };

  const handleAnalyze = () => {
    const formatDate = (date) => {
      if (!date) return null;
      return date.toISOString().split('.')[0];  // Remove milliseconds and Z
    };

    const requestData = {
      teams: selectedTeams.map(team => team.value),
      leagues: selectedLeagues.map(league => league.value),
      date_range: {
        start: formatDate(startDate),
        end: formatDate(endDate)
      }
    };

    if (selectedTeams.length > 0 || selectedLeagues.length > 0 || startDate || endDate) {
      console.log('Analyzing with filters:', requestData);
      fetch('http://localhost:5000/api/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestData),
      })
        .then(res => res.json())
        .then(data => {
          console.log('Analysis results:', data);
          setResults(data);
          setMatchedGames(data.matched_games || []);
        })
        .catch(err => {
          console.error('Error analyzing packages:', err);
          setResults({ single_packages: [], minimum_combination: null });
        });
    } else {
      setResults({ single_packages: [], minimum_combination: null });
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold text-center text-gray-900 mb-8">
          Package Analyzer
        </h1>

        <div className="filter-card mb-8">
          <h2 className="text-xl font-semibold text-gray-800 mb-6 pb-2 border-b">
            Filter Options
          </h2>

          <div className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* Teams Select */}
              <div>
                <label className="input-label">
                  Select Teams
                  <span className="ml-2 text-sm text-gray-500">
                    ({selectedTeams.length} selected)
                  </span>
                </label>
                <Select
                  isMulti
                  options={teams}
                  value={selectedTeams}
                  onChange={handleTeamChange}
                  placeholder="Select teams..."
                  className="filter-select"
                />
              </div>

              {/* Leagues Select */}
              <div>
                <label className="input-label">
                  Select Leagues
                  <span className="ml-2 text-sm text-gray-500">
                    ({selectedLeagues.length} selected)
                  </span>
                </label>
                <Select
                  isMulti
                  options={leagues}
                  value={selectedLeagues}
                  onChange={setSelectedLeagues}
                  placeholder={selectedTeams.length ? "Select leagues..." : "Select teams first"}
                  isDisabled={!selectedTeams.length}
                  className="filter-select"
                />
              </div>
            </div>

            {/* Date Range */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="input-label">Start Date</label>
                <div className="relative">
                  <DatePicker
                    selected={startDate}
                    onChange={date => setStartDate(date)}
                    selectsStart
                    startDate={startDate}
                    endDate={endDate}
                    placeholderText="Select start date"
                    className="w-full"
                    dateFormat="MMMM d, yyyy"
                    isClearable
                  />
                  <CalendarIcon className="absolute right-3 top-1/2 -translate-y-1/2 h-5 w-5 text-gray-400" />
                </div>
              </div>

              <div>
                <label className="input-label">End Date</label>
                <div className="relative">
                  <DatePicker
                    selected={endDate}
                    onChange={date => setEndDate(date)}
                    selectsEnd
                    startDate={startDate}
                    endDate={endDate}
                    minDate={startDate}
                    placeholderText="Select end date"
                    className="w-full"
                    dateFormat="MMMM d, yyyy"
                    isClearable
                  />
                  <CalendarIcon className="absolute right-3 top-1/2 -translate-y-1/2 h-5 w-5 text-gray-400" />
                </div>
              </div>
            </div>

            {/* Analyze Button */}
            <div className="flex justify-end">
              <button
                onClick={handleAnalyze}
                disabled={!selectedTeams.length && !selectedLeagues.length && !startDate && !endDate}
                className="inline-flex items-center px-6 py-3 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors duration-200"
              >
                <MagnifyingGlassIcon className="h-5 w-5 mr-2" />
                Analyze Packages
              </button>
            </div>
          </div>
        </div>

        {/* Results Section */}
        {/* ... rest of your results section with updated Tailwind classes ... */}
      </div>
    </div>
  );
}

export default App;
