import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import DatePicker from 'react-datepicker';
import { MagnifyingGlassIcon, CalendarIcon } from '@heroicons/react/24/outline';
import "react-datepicker/dist/react-datepicker.css";
import './App.css';

function GamesList({ games }) {
  return (
    <div className="games-list">
      <h3>Matched Games</h3>
      <div className="games-grid">
        {games.map(game => (
          <div key={game.id} className="game-card">
            <div className="game-date">{new Date(game.date).toLocaleString()}</div>
            <div className="game-teams">
              <span className="home-team">{game.home_team}</span>
              <span className="vs">vs</span>
              <span className="away-team">{game.away_team}</span>
            </div>
            <div className="game-tournament">{game.tournament}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

function App() {
  const [teams, setTeams] = useState([]);
  const [leagues, setLeagues] = useState([]);
  const [selectedTeams, setSelectedTeams] = useState([]);
  const [selectedLeagues, setSelectedLeagues] = useState([]);
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const [results, setResults] = useState({ single_packages: [], minimum_combination: null });
  const [matchedGames, setMatchedGames] = useState([]);

  const fetchFilteredLeagues = (selectedTeams) => {
    const teamValues = selectedTeams.map(team => team.value);

    fetch('http://localhost:5000/api/leagues/filtered', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ teams: teamValues }),
    })
      .then(res => res.json())
      .then(data => {
        const leagueOptions = data.map(league => ({
          value: league,
          label: league
        }));
        setLeagues(leagueOptions);
        setSelectedLeagues([]);
      })
      .catch(err => console.error('Error fetching filtered leagues:', err));
  };

  useEffect(() => {
    fetch('http://localhost:5000/api/teams')
      .then(res => res.json())
      .then(data => {
        const teamOptions = data.map(team => ({
          value: team,
          label: team
        }));
        setTeams(teamOptions);
      })
      .catch(err => console.error('Error fetching teams:', err));
  }, []);

  const handleTeamChange = (newSelectedTeams) => {
    setSelectedTeams(newSelectedTeams);
    fetchFilteredLeagues(newSelectedTeams);
  };

  const handleAnalyze = () => {
    const formatDate = (date) => {
      if (!date) return null;
      return date.toISOString().split('.')[0];  // Remove milliseconds and Z
    };

    const requestData = {
      teams: selectedTeams.map(team => team.value),
      leagues: selectedLeagues.map(league => league.value),
      date_range: {
        start: formatDate(startDate),
        end: formatDate(endDate)
      }
    };

    if (selectedTeams.length > 0 || selectedLeagues.length > 0 || startDate || endDate) {
      console.log('Analyzing with filters:', requestData);
      fetch('http://localhost:5000/api/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestData),
      })
        .then(res => res.json())
        .then(data => {
          console.log('Analysis results:', data);
          setResults(data);
          setMatchedGames(data.matched_games || []);
        })
        .catch(err => {
          console.error('Error analyzing packages:', err);
          setResults({ single_packages: [], minimum_combination: null });
        });
    } else {
      setResults({ single_packages: [], minimum_combination: null });
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="container mx-auto px-4 max-w-6xl">
        {/* Header */}
        <header className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-800 mb-2">
            Package Analyzer
          </h1>
          <p className="text-gray-600">
            Find the best streaming package combination for your favorite teams
          </p>
        </header>

        {/* Filters Section */}
        <section className="filters-container bg-white rounded-xl shadow-lg p-8 mb-8">
          <div className="flex items-center justify-between mb-8 pb-4 border-b border-gray-200">
            <h2 className="text-2xl font-semibold text-gray-800">
              Filter Options
            </h2>
            <button
              onClick={handleAnalyze}
              className="analyze-button"
              disabled={!selectedTeams.length && !selectedLeagues.length && !startDate && !endDate}
            >
              <MagnifyingGlassIcon className="h-5 w-5 mr-2" />
              Analyze Packages
            </button>
          </div>

          {/* Teams and Leagues */}
          <div className="space-y-8">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              {/* Teams Select */}
              <div className="filter-group">
                <label className="filter-label">
                  <div className="flex justify-between items-center mb-2">
                    <span className="text-sm font-semibold text-gray-700">Teams</span>
                    <span className="text-xs bg-blue-50 text-blue-600 px-2 py-1 rounded-full">
                      {selectedTeams.length} selected
                    </span>
                  </div>
                </label>
                <Select
                  isMulti
                  options={teams}
                  value={selectedTeams}
                  onChange={handleTeamChange}
                  placeholder="Select teams..."
                  className="filter-select"
                  classNamePrefix="filter-select"
                  theme={(theme) => ({
                    ...theme,
                    colors: {
                      ...theme.colors,
                      primary: '#3b82f6',
                      primary25: '#eff6ff',
                      primary50: '#dbeafe',
                      primary75: '#bfdbfe',
                    },
                  })}
                />
              </div>

              {/* Leagues Select */}
              <div className="filter-group">
                <label className="filter-label">
                  <div className="flex justify-between items-center mb-2">
                    <span className="text-sm font-semibold text-gray-700">Leagues</span>
                    <span className="text-xs bg-blue-50 text-blue-600 px-2 py-1 rounded-full">
                      {selectedLeagues.length} selected
                    </span>
                  </div>
                </label>
                <Select
                  isMulti
                  options={leagues}
                  value={selectedLeagues}
                  onChange={setSelectedLeagues}
                  placeholder={selectedTeams.length ? "Select leagues..." : "Select teams first"}
                  isDisabled={!selectedTeams.length}
                  className="filter-select"
                  classNamePrefix="filter-select"
                  theme={(theme) => ({
                    ...theme,
                    colors: {
                      ...theme.colors,
                      primary: '#3b82f6',
                      primary25: '#eff6ff',
                      primary50: '#dbeafe',
                      primary75: '#bfdbfe',
                    },
                  })}
                />
              </div>
            </div>

            {/* Date Range */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div className="filter-group">
                <label className="filter-label">
                  <span className="text-sm font-semibold text-gray-700 block mb-2">
                    Start Date
                  </span>
                </label>
                <div className="date-picker-wrapper">
                  <DatePicker
                    selected={startDate}
                    onChange={date => setStartDate(date)}
                    selectsStart
                    startDate={startDate}
                    endDate={endDate}
                    placeholderText="Select start date"
                    className="date-picker-input"
                    dateFormat="MMMM d, yyyy"
                    isClearable
                  />
                  <CalendarIcon className="h-5 w-5 date-picker-icon" />
                </div>
              </div>

              <div className="filter-group">
                <label className="filter-label">
                  <span className="text-sm font-semibold text-gray-700 block mb-2">
                    End Date
                  </span>
                </label>
                <div className="date-picker-wrapper">
                  <DatePicker
                    selected={endDate}
                    onChange={date => setEndDate(date)}
                    selectsEnd
                    startDate={startDate}
                    endDate={endDate}
                    minDate={startDate}
                    placeholderText="Select end date"
                    className="date-picker-input"
                    dateFormat="MMMM d, yyyy"
                    isClearable
                  />
                  <CalendarIcon className="h-5 w-5 date-picker-icon" />
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Results Section */}
        {(selectedTeams.length > 0 || selectedLeagues.length > 0 || startDate || endDate) && (
          <section className="results-container">
            {/* Active Filters Summary */}
            <div className="bg-white rounded-xl shadow-lg p-8 mb-8">
              <h2 className="text-2xl font-semibold text-gray-800 mb-6">Active Filters</h2>
              <div className="space-y-4">
                {selectedTeams.length > 0 && (
                  <div className="filter-summary">
                    <h3 className="text-sm font-semibold text-gray-600 mb-2">Teams</h3>
                    <div className="flex flex-wrap gap-2">
                      {selectedTeams.map(team => (
                        <span key={team.value} className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-blue-50 text-blue-700">
                          {team.label}
                        </span>
                      ))}
                    </div>
                  </div>
                )}

                {selectedLeagues.length > 0 && (
                  <div className="filter-summary">
                    <h3 className="text-sm font-semibold text-gray-600 mb-2">Leagues</h3>
                    <div className="flex flex-wrap gap-2">
                      {selectedLeagues.map(league => (
                        <span key={league.value} className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-green-50 text-green-700">
                          {league.label}
                        </span>
                      ))}
                    </div>
                  </div>
                )}

                {(startDate || endDate) && (
                  <div className="filter-summary">
                    <h3 className="text-sm font-semibold text-gray-600 mb-2">Date Range</h3>
                    <div className="flex flex-wrap gap-2">
                      {startDate && (
                        <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-purple-50 text-purple-700">
                          From: {startDate.toLocaleDateString()}
                        </span>
                      )}
                      {endDate && (
                        <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-purple-50 text-purple-700">
                          To: {endDate.toLocaleDateString()}
                        </span>
                      )}
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Matched Games */}
            {matchedGames.length > 0 && (
              <div className="bg-white rounded-xl shadow-lg p-8 mb-8">
                <GamesList games={matchedGames} />
              </div>
            )}

            {/* Package Results */}
            {results.minimum_combination && (
              <div className="bg-white rounded-xl shadow-lg p-8">
                <div className="optimal">
                  <div className="optimal-badge">Best Value</div>
                  <h3>Maximum Coverage with Minimum Package Combination</h3>
                  <div className="coverage-bar-container">
                    <div
                      className="coverage-bar"
                      style={{ width: `${results.minimum_combination.coverage_percentage}%` }}
                    >
                      {results.minimum_combination.coverage_percentage.toFixed(1)}%
                    </div>
                  </div>
                  <div className="package-details">
                    <div className="packages-needed">
                      <h4>Required Packages ({results.minimum_combination.num_packages}):</h4>
                      <ul>
                        {results.minimum_combination.packages.map((pkg, index) => (
                          <li key={index}>
                            <span className="package-name">{pkg.name}</span>
                            <span className="package-price">
                              {pkg.monthly_price !== null && pkg.monthly_price !== undefined ? (
                                <p>€{(pkg.monthly_price).toFixed(2)}/mo</p>
                              ) : (
                                <p>No monthly offer</p>
                              )}
                            </span>
                            <span className="package-price">€{(pkg.yearly_price * 12).toFixed(2)}/yr</span>
                          </li>
                        ))}
                      </ul>
                    </div>
                    <div className="total-costs">
                      <div className="monthly-total">
                        <h4>Monthly Subscription</h4>
                        {results.minimum_combination.monthly_cost !== null && results.minimum_combination.monthly_cost !== undefined ? (
                          <>
                           <p>€{(results.minimum_combination.monthly_cost).toFixed(2)}</p>
                           <p className="monthly-equivalent">(€{(results.minimum_combination.monthly_cost * 12).toFixed(2)}/yr)</p>
                          </>
                        ) : (
                          <p>No monthly offer</p>
                        )}

                      </div>
                      <div className="yearly-total">
                        <h4>Yearly Subscription</h4>
                        <p>€{(results.minimum_combination.yearly_cost * 12).toFixed(2)}</p>
                        <p className="monthly-equivalent">(€{(results.minimum_combination.yearly_cost).toFixed(2)}/mo)</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </section>
        )}
      </div>
    </div>
  );
}

export default App;
