import React, { useState, useEffect } from 'react';
import { fetchTeamLogo } from '../services/teamService';
import { defaultLogoUrl, getTeamLogoUrl } from '../utils/teamLogos';

export const TeamOption = ({ data, innerProps, ...props }) => {
  const [logoUrl, setLogoUrl] = useState(defaultLogoUrl);

  useEffect(() => {
    fetchTeamLogo(data.value)
      .then(url => {
        if (url) setLogoUrl(url);
      });
  }, [data.value]);

  return (
    <div className="team-option" {...innerProps} {...props}>
      <img 
        src={logoUrl}
        alt={`${data.label} logo`}
        className="team-logo"
        onError={(e) => {
          e.target.onerror = null;
          e.target.src = defaultLogoUrl;
        }}
      />
      <span>{data.label}</span>
    </div>
  );
};

export const TeamValue = ({ data, ...props }) => {
  return (
    <div className="team-value" {...props}>
      <img 
        src={getTeamLogoUrl(data.value)} 
        alt={`${data.label} logo`}
        className="team-logo-small"
        onError={(e) => {
          e.target.onerror = null;
          e.target.src = defaultLogoUrl;
        }}
      />
      <span>{data.label}</span>
    </div>
  );
};
