import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import './App.css';

function App() {
  const [teams, setTeams] = useState([]);
  const [selectedTeams, setSelectedTeams] = useState([]);
  const [results, setResults] = useState({ single_packages: [], minimum_combinations: null });

  useEffect(() => {
    fetch('http://localhost:5000/api/teams')
      .then(res => res.json())
      .then(data => {
        const teamOptions = data.map(team => ({
          value: team,
          label: team
        }));
        setTeams(teamOptions);
      })
      .catch(err => console.error('Error fetching teams:', err));
  }, []);

  const handleTeamSelect = (selectedOptions) => {
    const selected = selectedOptions ? selectedOptions.map(option => option.value) : [];
    setSelectedTeams(selected);

    if (selected.length > 0) {
      console.log('Analyzing teams:', selected);
      fetch('http://localhost:5000/api/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ teams: selected }),
      })
        .then(res => res.json())
        .then(data => {
          console.log('Analysis results:', data);
          setResults(data);
        })
        .catch(err => {
          console.error('Error analyzing packages:', err);
          setResults({ single_packages: [], minimum_combinations: null });
        });
    } else {
      setResults({ single_packages: [], minimum_combinations: null });
    }
  };

  return (
    <div className="App">
      <h1>Streaming Package Comparison</h1>

      <div className="team-selector">
        <h2>Select Teams</h2>
        <Select
          isMulti
          options={teams}
          onChange={handleTeamSelect}
          placeholder="Select multiple teams..."
          className="team-select-container"
          classNamePrefix="team-select"
        />
      </div>

      {selectedTeams.length > 0 && (
        <div className="results">
          <div className="selected-teams-display">
            <h3>Selected Teams:</h3>
            <div className="team-tags">
              {selectedTeams.map(team => (
                <span key={team} className="team-tag">{team}</span>
              ))}
            </div>
          </div>

          {results.minimum_combinations && (
            <div className="minimum-combinations">
              <h2>Minimum Package Combinations</h2>
              <p className="combinations-info">
                Minimum packages needed: {results.minimum_combinations.min_packages_needed}
              </p>
              <div className="combinations-grid">
                {results.minimum_combinations.combinations.map((combo, index) => (
                  <div key={index} className="package-card optimal">
                    <div className="optimal-badge">Combination {index + 1}</div>
                    <h3>Package Combination</h3>
                    <div className="coverage-bar-container">
                      <div 
                        className="coverage-bar" 
                        style={{width: `${combo.coverage_percentage}%`}}
                      >
                        {combo.coverage_percentage.toFixed(1)}%
                      </div>
                    </div>
                    <div className="package-details">
                      <div className="packages-needed">
                        <h4>Required Packages ({combo.num_packages}):</h4>
                        <ul>
                          {combo.packages.map((pkg, i) => (
                            <li key={i}>
                              <span className="package-name">{pkg.name}</span>
                              <span className="package-price">€{pkg.yearly_price}/year</span>
                            </li>
                          ))}
                        </ul>
                      </div>
                      <div className="total-costs">
                        <div className="yearly-total">
                          <h4>Total Yearly Cost</h4>
                          <p>€{combo.yearly_cost}</p>
                        </div>
                        <div className="monthly-total">
                          <h4>Total Monthly Cost</h4>
                          <p>€{combo.monthly_cost}</p>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          <h2>All Package Rankings</h2>
          {results.single_packages.length === 0 ? (
            <div className="no-results">
              <p>No streaming packages found for the selected team(s).</p>
              <p>This might be because:</p>
              <ul>
                <li>The selected team(s) don't have any scheduled matches</li>
                <li>No streaming service covers these matches</li>
                <li>The matches are outside the current season</li>
              </ul>
            </div>
          ) : (
            <div className="package-rankings">
              {results.single_packages.map((result, index) => (
                <div key={index} className="package-card">
                  <div className="rank-badge">{index + 1}</div>
                  <h3>{result.package_name}</h3>
                  <div className="coverage-bar-container">
                    <div 
                      className="coverage-bar" 
                      style={{width: `${result.coverage_percentage}%`}}
                    >
                      {result.coverage_percentage.toFixed(1)}%
                    </div>
                  </div>
                  <div className="package-details">
                    <div className="games-coverage">
                      <p>Games covered: {result.covered_games} / {result.total_games}</p>
                      <p className="coverage-note">
                        {result.coverage_percentage < 100 
                          ? "⚠️ Doesn't cover all games" 
                          : "✅ Full coverage"}
                      </p>
                    </div>
                    <div className="pricing-comparison">
                      <div className="monthly-price">
                        <h4>Monthly</h4>
                        <p>€{result.monthly_price}/month</p>
                        <p className="yearly-equivalent">
                          (€{(result.monthly_price * 12).toFixed(2)}/year)
                        </p>
                      </div>
                      <div className="yearly-price">
                        <h4>Yearly</h4>
                        <p>€{result.yearly_price}/year</p>
                        <p className="monthly-equivalent">
                          (€{(result.yearly_price / 12).toFixed(2)}/month)
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
